import java.io.File.*;
import jxl.*;
import jxl.write.*;

Workbook workbook1 = Workbook.getWorkbook(new File("C:\\SDET\\ReadyAPI Assignment\\TestData.xls")) 
Sheet data = workbook1.getSheet("Scenario2");

row_size= data.getRows().toInteger() 
Col_size = data.getColumns()
//log.info Col_size
//log.info row_size

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def myTestCase = context.testCase
//String [] credential = null;
def credential = []
for (int i =0; i<Col_size; i++)
{

	Cell cell = data.getCell(i,1)
	log.info ( cell.getString())
	credential.add(cell.getString())

}



myTestCase.setPropertyValue("Username", credential.getAt(0))
myTestCase.setPropertyValue("firstName", credential.getAt(1))
myTestCase.setPropertyValue("lastName", credential.getAt(2))
myTestCase.setPropertyValue("email", credential.getAt(3))
myTestCase.setPropertyValue("password", credential.getAt(4))
myTestCase.setPropertyValue("phone", credential.getAt(5))
myTestCase.setPropertyValue("UsernameUpdate", credential.getAt(6))
myTestCase.setPropertyValue("firstNameUpdated", credential.getAt(7))
myTestCase.setPropertyValue("lastNameUpdated", credential.getAt(8))
myTestCase.setPropertyValue("emailUpdated", credential.getAt(9))
myTestCase.setPropertyValue("passwordupdated", credential.getAt(10))
myTestCase.setPropertyValue("phoneupdated", credential.getAt(11))

